scenes.script:
(ctx) => {
  return {
    animateTitle: () => {
      const bar = ctx.root.querySelector('#sub-bar');
      bar.style.width = '60%';
      bar.style.transition = 'width 2s cubic-bezier(0.65, 0, 0.35, 1)';
    }
  }
}

(ctx) => {
  return {
    typeCode: (id) => {
       const el = ctx.root.querySelector(id);
       el.classList.remove('opacity-0');
       el.classList.add('animate-pulse');
    }
  }
}

(ctx) => {
  let sketch = (p) => {
    let particles = [];
    const num = 1000;
    const noiseScale = 0.01;

    p.setup = () => {
      p.createCanvas(window.innerWidth, window.innerHeight).parent('p5-canvas-container');
      for(let i=0; i<num; i++) { particles.push(p.createVector(p.random(p.width), p.random(p.height))); }
      p.stroke(52, 211, 153, 50);
    };

    p.draw = () => {
      p.background(0, 10);
      for(let i=0; i<num; i++) {
        let p_vec = particles[i];
        p.point(p_vec.x, p_vec.y);
        let n = p.noise(p_vec.x * noiseScale, p_vec.y * noiseScale);
        let a = p.TAU * n;
        p_vec.x += p.cos(a);
        p_vec.y += p.sin(a);
        if(!p.onScreen(p_vec)) {
          p_vec.x = p.random(p.width);
          p_vec.y = p.random(p.height);
        }
      }
    };

    p.onScreen = (v) => v.x >= 0 && v.x <= p.width && v.y >= 0 && v.y <= p.height;
  };

  const p5Instance = ctx.p5(sketch, 'p5-canvas-container');

  return {
    remove: () => p5Instance.remove()
  }
}

(ctx) => {
  const config = {
    type: 'bar',
    data: {
      labels: ['JS (p5/Three)', 'Python (AI)', 'C++ (OF)', 'GLSL (Shaders)'],
      datasets: [{
        label: 'Creative Usage',
        data: [95, 82, 60, 75],
        backgroundColor: ['#10b981', '#3b82f6', '#f43f5e', '#f59e0b'],
        borderRadius: 10
      }]
    },
    options: {
      responsive: true,
      maintainAspectRatio: false,
      animation: { duration: 2000, easing: 'easeOutQuart' },
      plugins: { legend: { display: false } }
    }
  };

  let chartInstance = null;

  return {
    renderChart: () => {
      chartInstance = ctx.chart('lang-chart', config);
    }
  }
}
